import React, { Component } from "react";
import axios from 'axios';
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "../../actions/authActions";
import classnames from "classnames";

class EditProfile extends Component {
    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutUser();
    };

    constructor(props) {
        super(props);
        this.state = {userdetails: [], errors: {}};
    }

    componentDidMount() {
        const { user } = this.props.auth;
        axios.get('http://localhost:4000/user/'+ user.id)
             .then(response => {
                 this.setState({userdetails: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
            errors: nextProps.errors
            });
        }
    }

    onSubmit = e => {
        e.preventDefault();
        const user = this.state.userdetails;
        const editedUser = {
            name: user.name,
            email: user.email,
            password: user.password,
            password2: user.password2,
            role: user.role,
            phone_number: user.phone_number,
            bio: user.bio,
            education: user.education,
            resume: user.resume,
            skills: user.skills
        };
        axios
            .post('http://localhost:4000/user/edit_profile' + user.id, editedUser)
            .then(response => {
                console.log(editedUser);
            })
            .catch(function(error) {
                console.log(error);
            })
    };

    render() {
        const user = this.state.userdetails;
        const { errors } = this.state;
        const userRole = user.role;
        let EditUserDetails;
        if(userRole == 'applicant') {
            EditUserDetails = 
            <form noValidate onSubmit={this.onSubmit}>
                <div className="input-field col s12">
                    <label htmlFor="name">Role</label><br></br>
                    <select 
                        value={this.state.role} 
                        onChange={this.handleChange}
                        error={errors.role}
                        id="role"
                        className={classnames("", {
                            invalid: errors.role
                            })}className={classnames("", {
                            invalid: errors.role
                        })}
                    >
                        <option value="">Select role</option>
                        <option value="applicant">Applicant</option>
                        <option value="recruiter">Recruiter</option>
                    </select>
                    <span className="red-text">{errors.role}</span>
                </div> 
                <div className="input-field col s12">
                    <label htmlFor="name">Name</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={this.state.name}
                        error={errors.name}
                        id="name"
                        type="text"
                        className={classnames("", {
                            invalid: errors.name
                            })}className={classnames("", {
                            invalid: errors.name
                        })}
                    />
                    <span className="red-text">{errors.name}</span>
                </div>
                <div className="input-field col s12">
                    <label htmlFor="email">Email</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={this.state.email}
                        error={errors.email}
                        id="email"
                        type="email"
                        className={classnames("", {
                            invalid: errors.email
                        })}
                    />
                    <span className="red-text">{errors.email}</span>
                </div>
                <div className="input-field col s12">
                    <label htmlFor="password">Password</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={this.state.password}
                        error={errors.password}
                        id="password"
                        type="password"
                        className={classnames("", {
                            invalid: errors.password
                        })}
                    />
                    <span className="red-text">{errors.password}</span>
                </div>
                <div className="input-field col s12">
                    {RoleForm}
                </div>
                <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                    <button
                        style={{
                        width: "150px",
                        borderRadius: "3px",
                        letterSpacing: "1.5px",
                        marginTop: "1rem"
                        }}
                        type="submit"
                        className="btn btn-primary btn-large waves-effect waves-light hoverable blue accent-3"
                    >
                        Sign up
                    </button>
                </div>
            </form>
        }
        else if(userRole == 'recruiter') {
            EditUserDetails = 
            <ul>
                <li>DP: {user.profile_image}</li>
                <li>Bio: {user.bio}</li>
                <li>Email: {user.email}</li>
                <li>Contact No.: {user.phone_number}</li>
            </ul>
        }
        return (
            <div style={{ height: "75vh" }} className="container valign-wrapper">
                <div className="row">
                    <div className="col s12 center-align">
                    <Card style={{ width: '100%' }}>
                        <Card.Header>
                            <Button variant="light"><h4>My Profile</h4></Button>
                            <Button variant="light"><i className="material-icons">edit</i></Button>
                        </Card.Header>
                        <Card.Body>
                            <Card.Title>{user.name}</Card.Title>
                            <Card.Text>
                                {EditUserDetails}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                    </div>
                </div>
            </div>
        );
    }
}

EditProfile.propTypes = {
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
});
export default connect(
    mapStateToProps,
)(EditProfile);
