import React, { Component } from "react";
import axios from 'axios';
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class EditProfile extends Component {
    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutUser();
    };

    constructor(props) {
        super(props);
        this.state = {
            recruiter: [],
            title: "", //
            description: "",
            type: "", //
            duration: -1, //
            salary: -1, //
            maxpos: -1,
            maxapp: -1,
            address: "", //
            skills: [], //
            skillsstring: "",
            rating: 5,
            dateOfPost: new Date(),
            deadline: new Date() //
        }
    };

    componentDidMount() {
        const { user } = this.props.auth;
    }

    validate = () => {
        // let nameError = "";
        // let emailError = "";

        // if (!this.state.name) {
        //   nameError = "Name cannot be blank";
        // }

        // if (!this.state.email.includes("@")) {
        //   emailError = "Invalid email";
        // }

        // if (emailError || nameError) {
        //   this.setState({ emailError, nameError });
        //   return false;
        // }

        return true;
    };

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
            errors: nextProps.errors
            });
        }
    }

    onSubmit = e => {
        e.preventDefault();
        const { user } = this.props.auth;
        const euser = this.state;
        euser.skills = this.state.skillsstring.split(',');
        const newJob = {
            recruiter: user.id,
            title: euser.title,
            description: euser.description,
            type: euser.type,
            duration: euser.duration,
            salary: euser.salary,
            maxpos: euser.maxpos,
            maxapp: euser.maxapp,
            address: euser.address,
            skills: euser.skills,
            rating: euser.rating,
            dateOfPost: euser.dateOfPost,
            deadline: euser.deadline
        };
        const isValid = this.validate();
        if (isValid) {
            axios
                .put('http://localhost:4000/job/add_job', newJob)
                .then(response => {
                    console.log(editedUser);
                })
                .catch(function(error) {
                    console.log(error);
                })
            // to refresh
            this.props.history.push("/viewJobs");
            this.props.history.push("/viewJobs");
            this.props.history.goBack();
        }
    };

    render() {
        const user = this.state;
        const userRole = user.role;
        user.skillsstring = user.skills.toString()
        let EditUserDetails;
        if(userRole == 'applicant') {
            EditUserDetails = 
            <form noValidate onSubmit={this.onSubmit}>
                <div className="input-field col s12">
                    <label htmlFor="name">Name</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.name}
                        id="name"
                        type="text"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div>
                </div>
                <div className="input-field col s12">
                    <label htmlFor="email">Email</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.email}
                        id="email"
                        type="email"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.emailError}
                    </div>
                </div>
                <div className="input-field col s12">
                    <label htmlFor="skills">Skills</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.skillsstring}
                        id="skills"
                        type="text"
                    />
                </div>
                <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                    <button
                        style={{
                        width: "150px",
                        borderRadius: "3px",
                        letterSpacing: "1.5px",
                        marginTop: "1rem"
                        }}
                        type="submit"
                        className="btn btn-primary btn-large waves-effect waves-light hoverable blue accent-3"
                    >
                        Save
                    </button>
                </div>
            </form>
        }
        else if(userRole == 'recruiter') {
            EditUserDetails = 
            <form noValidate onSubmit={this.onSubmit}>
                <div className="input-field col s12">
                    <label htmlFor="name">Name</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.name}
                        id="name"
                        type="text"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div>
                </div>
                <div className="input-field col s12">
                    <label htmlFor="bio">Bio.</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.bio}
                        id="bio"
                        type="text"
                    />
                </div>
                <div className="input-field col s12">
                    <label htmlFor="phone_number">Phone no.</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.phone_number}
                        id="phone_number"
                        type="number"
                    />
                </div>
                <div className="input-field col s12">
                    <label htmlFor="email">Email</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.email}
                        id="email"
                        type="email"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.emailError}
                    </div>
                </div>
                <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                    <button
                        style={{
                        width: "150px",
                        borderRadius: "3px",
                        letterSpacing: "1.5px",
                        marginTop: "1rem"
                        }}
                        type="submit"
                        className="btn btn-primary btn-large waves-effect waves-light hoverable blue accent-3"
                    >
                        Save
                    </button>
                </div>
            </form>
        }
        return (
            <div style={{ height: "75vh" }} className="container valign-wrapper">
                <div className="row">
                    <div className="col s12 center-align">
                        <Card style={{ width: '100%' }}>
                            <Card.Header>
                                <Button variant="light"><h4>My Profile</h4></Button>
                            </Card.Header>
                            <Card.Body>
                                <Card.Text>
                                    {EditUserDetails}
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </div>
                </div>
            </div>
        );
    }
}

EditProfile.propTypes = {
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
});
export default connect(
    mapStateToProps,
)(EditProfile);
