import React, { Component } from "react";
import axios from 'axios';
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import Tooltip from '@material-ui/core/Tooltip';

class Profile extends Component {
    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutUser();
    };

    constructor(props) {
        super(props);
        this.state = {userdetails: []};
    }

    componentDidMount() {
        const { user } = this.props.auth;
        axios.get('http://localhost:4000/user/'+ user.id)
             .then(response => {
                 this.setState({userdetails: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    delEducation(ed) {
        const { user } = this.props.auth;
        const idToRemove = ed._id;
        this.state.userdetails.education = this.state.userdetails.education.filter((item) => item._id !== idToRemove);
        axios
            .put('http://localhost:4000/user/edit_profile/' + user.id, this.state.userdetails)
            .then(response => {
                console.log(this.state.userdetails);
            })
            .catch(function(error) {
                console.log(error);
            })
        // to refresh
        this.props.history.push("/profile");
        this.props.history.push("/profile");
        this.props.history.goBack();
    }

    render() {
        const user = this.state.userdetails;
        const userRole = user.role;
        let UserDetails;
        if(userRole == 'applicant') {
            UserDetails = 
            <div>
                 <ul>
                    {/* <li>DP: {user.profile_image}</li> */}
                    <li>Email: {user.email}</li>
                    <li>Skills: 
                        <ul>
                            {user.skills.map(skill => (
                                <li>{skill}</li>
                            ))}
                        </ul>
                    </li>
                    {/* <li>Resume: {user.resume}</li> */}
                </ul>
                <hr></hr>
                <ul>
                    <li>Education
                        <Tooltip title="Add Education" aria-label="added">
                            <Link style={{ color: '#009900', weight: 'bold', size: '18px' }} to="/addeducation"><i className="material-icons">: add</i></Link>
                        </Tooltip>
                        <ul>
                            {user.education.map(ed => (
                                <li>
                                    <ul>
                                        <li>School: {ed.school}</li>
                                        <li>Degree: {ed.degree}</li>
                                        <li>Start date: {ed.startdate ? ed.startdate.substring(0, 10): "NA"}</li>
                                        <li>End date: {ed.enddate ? ed.enddate.substring(0, 10):"NA"}</li>
                                    </ul>
                                    
                                    <Tooltip title="Delete Above Education" aria-label="delete">
                                    <button
                                    style={{
                                        color: "#FF0000",
                                        }}
                                        className="btn btn-small waves-effect waves-light hoverable blue accent-3" 
                                        onClick={() => this.delEducation(ed)}>
                                        <i className="material-icons">delete</i>
                                    </button>
                                    </Tooltip>
                                    <Tooltip title="Edit Above Education" aria-label="edit">
                                    <button
                                    style={{
                                        color: "#0000FF",
                                        }}
                                        className="btn btn-small waves-effect waves-light hoverable blue accent-3" 
                                        onClick={() => this.delEducation(ed)}>
                                        <i className="material-icons">delete</i>
                                    </button>
                                    </Tooltip>
                                    <br></br>
                                </li>
                            ))}
                        </ul>
                    </li>
                </ul>
            </div>
        }
        else if(userRole == 'recruiter') {
            UserDetails = 
            <ul>
                <li>DP: {user.profile_image}</li>
                <li>Bio: {user.bio}</li>
                <li>Email: {user.email}</li>
                <li>Contact No.: {user.phone_number}</li>
            </ul>
        }
        return (
            <div style={{ height: "75vh" }} className="container valign-wrapper">
                <div className="row">
                    <div className="col s12 center-align">
                    <Card style={{ width: '100%' }}>
                        <Card.Header>
                            <Button variant="light"><h4>My Profile</h4></Button>
                        </Card.Header>
                        <Card.Body>
                            <Card.Title>
                                <p>
                                    <b>{user.name}</b>
                                    <Tooltip title="Edit Profile" aria-label="edit">
                                        <Link to="/editprofile"><i className="material-icons">: edit</i></Link>
                                    </Tooltip>
                                </p>
                            </Card.Title>
                            <Card.Text>
                                {UserDetails}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                    </div>
                </div>
            </div>
        );
    }
}

Profile.propTypes = {
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth
});
export default connect(
    mapStateToProps,
)(Profile);
