import React, { Component } from "react";
import axios from 'axios';
import Card from "react-bootstrap/Card";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class CreateJob extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userdetails: [],
            recruiter: [],
            title: "",
            description: "",
            type: "",
            duration: -1,
            durationstr: "",
            salary: -1,
            // default max no of pos and app = 100
            maxpos: 100,
            maxapp: 100,
            address: "",
            skills: [],
            skillstr: "",
            rating: 5,
            dateOfPost: new Date(),
            // default 100 days from now
            myDate: new Date(new Date().getTime()+(100*24*60*60*1000))
        }
    };

    componentDidMount() {
        const { user } = this.props.auth;
        axios.get('http://localhost:4000/user/'+ user.id)
             .then(response => {
                 this.setState({userdetails: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    validate = () => {
        // let nameError = "";
        // let emailError = "";

        // if (!this.state.name) {
        //   nameError = "Name cannot be blank";
        // }

        // if (!this.state.email.includes("@")) {
        //   emailError = "Invalid email";
        // }

        // if (emailError || nameError) {
        //   this.setState({ emailError, nameError });
        //   return false;
        // }

        return true;
    };

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
            errors: nextProps.errors
            });
        }
    }

    onSubmit = e => {
        e.preventDefault();
        let euser = this.state;
        euser.skills = this.state.skillstr.split(',');
        const newJob = {
            recruiter: this.state.userdetails,
            title: euser.title,
            description: euser.description,
            type: euser.type,
            duration: euser.duration,
            salary: euser.salary,
            maxpos: euser.maxpos,
            maxapp: euser.maxapp,
            address: euser.address,
            skills: euser.skills,
            rating: euser.rating,
            dateOfPost: euser.dateOfPost,
            deadline: euser.deadline
        };
        const isValid = this.validate();
        if (isValid) {
            axios
                .post('http://localhost:4000/job/add_job', newJob)
                .then(response => {
                    console.log(newJob);
                    alert("Job added successfully!");
                })
                .catch(function(error) {
                    console.log(error);
                    alert("Job NOT added successfully!");
                })
            // // to refresh
            // this.props.history.push("/viewJobs");
            // this.props.history.push("/viewJobs");
            // this.props.history.goBack();
        }
    };

    render() {
        const user = this.state;
        const userRole = this.state.userdetails.role;
        let AddJob;
        if(userRole == "recruiter") {
            AddJob = 
            <form noValidate onSubmit={this.onSubmit}>
                <div className="input-field col s12">
                    <label htmlFor="title">Title</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.title}
                        id="title"
                        type="text"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.titleError}
                    </div>
                </div>
                <div className="input-field col s12">
                    <label htmlFor="description">Description</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.description}
                        id="description"
                        type="text"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="type">Type</label><br></br>
                    <select 
                        value={this.state.type} 
                        onChange={this.onChange}
                        id="type"
                    >
                        <option value="">Select type</option>
                        <option value="fullTime">Full-time</option>
                        <option value="partTime">Part-time</option>
                        <option value="wfh">Work from home</option>
                    </select>
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.typeError}
                    </div>
                </div> 
                <div className="input-field col s12">
                    <label htmlFor="salary">Salary</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.salary}
                        id="salary"
                        type="number"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="duration">Type</label><br></br>
                    <select 
                        value={this.state.durationstr} 
                        onChange={this.onChange}
                        id="type"
                    >
                        <option value="">Select duration</option>
                        <option value = "0" >Indefinite</option>
                        <option value="1">1 month</option>
                        <option value="2">2 months</option>
                        <option value="3">3 months</option>
                        <option value="4">4 months</option>
                        <option value="5">5 months</option>
                        <option value="6">6 months</option>
                    </select>
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.durationError}
                    </div>
                </div> 
                <div className="input-field col s12">
                    <label htmlFor="maxpos">Max. no. of positions available</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.maxpos}
                        id="maxpos"
                        type="number"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="maxapp">Max. total no. applications allowed</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.maxapp}
                        id="maxapp"
                        type="number"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="address">Address</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.address}
                        id="address"
                        type="text"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.addressError}
                    </div>
                </div>
                <div className="input-field col s12">
                    <label htmlFor="skillstr">Skills</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.skillstr}
                        id="skillstr"
                        type="text"
                    />
                </div>
                <div className="input-field col s12">
                    <label htmlFor="dateOfPost">Date of Post</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.dateOfPost}
                        id="dateOfPost"
                        type="date"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="deadline">Deadline</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.deadline}
                        id="deadline"
                        type="date"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                    <button
                        style={{
                        width: "150px",
                        borderRadius: "3px",
                        letterSpacing: "1.5px",
                        marginTop: "1rem"
                        }}
                        type="submit"
                        className="btn btn-primary btn-large waves-effect waves-light hoverable blue accent-3"
                    >
                        Add Job
