import React, {Component} from 'react';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import List from '@material-ui/core/List';
import Tooltip from '@material-ui/core/Tooltip';
import ListItem from '@material-ui/core/ListItem';
import PropTypes from "prop-types";
import Card from "react-bootstrap/Card";
import { connect } from "react-redux";
import Dropdown from 'react-bootstrap/Dropdown';
import { Link } from "react-router-dom";



class MyActiveJobs extends Component {

    constructor(props) {
        super(props);
        this.state = {
            userdetails: [],
            jobs: [],
            sortedJobs: [], 
            sortName:true, 
            showform: false,
            posmax: -1,
            appmax: -1,
            deadline: new Date(),
            editing: ""
        };
    }

    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutUser();
    };

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    componentDidMount() {
        this.state.showform = false;
        const { user } = this.props.auth;
        axios.get('http://localhost:4000/user/'+ user.id)
                .then(response => {
                    this.setState({userdetails: response.data});
                })
                .catch(function(error) {
                    console.log(error);
                })
        axios.get('http://localhost:4000/job/get_jobs')
            .then(response => {
                this.setState({jobs: response.data, sortedJobs:response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
    }


    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
            errors: nextProps.errors
            });
        }
    }

    deljob(id) {
        axios
            .delete('http://localhost:4000/job/del_job/' + id)
            .then(response => {
                console.log("Job deleted successfully.");
            })
            .catch(function(error) {
                console.log(error);
            })

        this.state.applications.filter(item => item.jobId === id && item.status === "Accepted").map((appli,ind) => 
        {
            const editEmployee = {
                working: false
            }
            axios
                .put('http://localhost:4000/user/edit_profile/' + appli.applicantId, editEmployee)
                .then(response => {
                    console.log(editEmployee);
                })
                .catch(function(error) {
                    console.log(error);
                })
        })

        this.state.applications.filter(item => item.jobId === id).map((appli,ind) => 
        {
            let applicant = this.getapplicant(appli.applicantId);
            let nnumapp = +applicant.numapp - 1;

            const editApplicant = {
                numapp: nnumapp
            };

            axios
                .put('http://localhost:4000/user/edit_profile/' + appli.applicantId, editApplicant)
                .then(response => {
                    console.log(editApplicant);
                })
                .catch(function(error) {
                    console.log(error);
                })

            const editAppli = {
                status: "Deleted"
            }
            axios
                .put('http://localhost:4000/application/edit_application/' + appli._id, editAppli)
                .then(response => {
                    console.log(editAppli);
                })
                .catch(function(error) {
                    console.log(error);
                })
        })
        // to refresh
        window.location.reload();
    }

    editJob(job) {
        this.state.showform = !this.state.showform;
        this.state.editing = job._id;
        console.log(this.state.showform);
        this.state.appmax = job.appmax;
        this.state.posmax = job.posmax;
        if(job.deadline)
        {
            job.deadline = job.deadline.toString();
            job.deadline = job.deadline.substring(0,10);
            this.state.deadline = job.deadline;
        }
        // to refresh
        window.location.reload();
    }

    onBack() {
        this.state.showform = !this.state.showform;
        this.state.editing = "";

        // to refresh
        window.location.reload();
    }

    editJobSubmit(job) {
        const idToChange = job._id;
        // alert(this.state.posmax);
        const ind = this.state.jobs.findIndex(x => x._id === idToChange)
        if(this.state.appmax)
            this.state.jobs[ind].appmax = this.state.appmax;
        if(this.state.posmax)
            this.state.jobs[ind].posmax = this.state.posmax;
        if(this.state.deadline)
            this.state.jobs[ind].deadline = this.state.deadline;
        // alert(this.state.jobs[ind].posmax);
        axios
            .put('http://localhost:4000/job/edit_job/' + idToChange, this.state.jobs[ind])
            .then(response => {
                console.log(this.state.jobs[ind]);
                this.state.editing = "";
            })
            .catch(function(error) {
                alert("Job couldn't be updated!");
                console.log(error);
            })
        // to refresh

        this.state.showform = !this.state.showform;
        window.location.reload();
    }

    // sortChange(){
    //     var array = this.state.users;
    //     var flag = this.state.sortName;
    //     array.sort(function(a, b) {
    //         if(a.date != undefined && b.date != undefined){
    //             return (1 - flag*2) * (new Date(a.date) - new Date(b.date));
    //         }
    //         else{
    //             return 1;
    //         }
    //       });
    //     this.setState({
    //         users:array,
    //         sortName:!this.state.sortName,
    //     })
    // }

    // renderIcon(){
    //     if(this.state.sortName){
    //         return(
    //             <ArrowDownwardIcon/>
    //         )
    //     }
    //     else{
    //         return(
    //             <ArrowUpwardIcon/>
    //         )            
    //     }
    // }

    render() 
