import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "../../actions/authActions";
import classnames from "classnames";

class Register extends Component {
    constructor() {
        super();
        this.state = {
            name: "",
            email: "",
            password: "",
            password2: "",
            role: "",
            phone_number: NaN,
            skills: [],
            errors: {}
        };
        this.handleChange = this.handleChange.bind(this);
    }
    addskill = e => {
        e.preventDefault();
        this.setState(({ skills, skill }) => ({
          skills: [...skills, ...skill.split(",")],
          skill: ""
        }));
    };  
    removeskill(index) {
        this.setState({
          skills: this.state.skills.filter((_, i) => i !== index)
        });
    }      
    handleChange(event) {
        this.setState({role: event.target.value});
    }
    componentDidMount() {
        // If logged in and user navigates to Register page, should redirect them to dashboard
        if (this.props.auth.isAuthenticated) {
            this.props.history.push("/dashboard");
        }
    }
    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
            errors: nextProps.errors
            });
        }
    }
    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };
    onSubmit = e => {
        e.preventDefault();
        this.state.skills = this.state.skills.split(',');
        const newUser = {
            name: this.state.name,
            email: this.state.email,
            password: this.state.password,
            password2: this.state.password2,
            role: this.state.role,
            phone_number: this.state.phone_number,
            skills: this.state.skills
        };
        this.props.registerUser(newUser, this.props.history);
        console.log(newUser);
    };
    render() {
        const { errors } = this.state;
        const userRole = this.state.role;
        let RoleForm;
        if(userRole == 'applicant') {
            RoleForm = 
            <div>
                <label>Skills (comma-separated):</label><br></br>
                <input
                type="text"
                value={this.state.skills}
                placeholder="Enter skills"
                onChange={this.onChange}
                />
            </div>
        }
        else if(userRole == 'recruiter') {
            RoleForm = 
            <div>
                <label htmlFor="phone_number">Phone no.</label><br></br>
                <input
                    onChange={this.onChange}
                    value={this.state.phone_number}
                    id="phone_number"
                    type="number"
                />
            </div>
        }
        return (
            <div className="container">
                <div className="row">
                    <div className="col s8 offset-s2">
                    <Link to="/" className="btn-flat waves-effect">
                        <i className="material-icons left"><b>keyboard_backspace</b></i>
                    </Link>
                    <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                        <h4>
                        <b>Register</b>
                        </h4>
                        <p className="grey-text text-darken-1">
                        Already have an account? <Link to="/login">Log in</Link>
                        </p>
                    </div>
                        <form noValidate onSubmit={this.onSubmit}>
                            <div className="input-field col s12">
                                <label htmlFor="name">Role</label><br></br>
                                <select 
                                    value={this.state.role} 
                                    onChange={this.handleChange}
                                    error={errors.role}
                                    id="role"
                                    className={classnames("", {
                                        invalid: errors.role
                                        })}className={classnames("", {
                                        invalid: errors.role
                                    })}
                                >
                                    <option value="">Select role</option>
                                    <option value="applicant">Applicant</option>
                                    <option value="recruiter">Recruiter</option>
                                </select>
                                <span className="red-text">{errors.role}</span>
                            </div> 
                            <div className="input-field col s12">
                                <label htmlFor="name">Name</label><br></br>
                                <input
                                    onChange={this.onChange}
                                    value={this.state.name}
                                    error={errors.name}
                                    id="name"
                                    type="text"
                                    className={classnames("", {
                                        invalid: errors.name
                                        })}className={classnames("", {
                                        invalid: errors.name
                                    })}
                                />
                                <span className="red-text">{errors.name}</span>
                            </div>
                            <div className="input-field col s12">
                                <label htmlFor="email">Email</label><br></br>
                                <input
                                    onChange={this.onChange}
                                    value={this.state.email}
                                    error={errors.email}
                                    id="email"
                                    type="email"
                                    className={classnames("", {
                                        invalid: errors.email
                                    })}
                                />
                                <span className="red-text">{errors.email}</span>
                            </div>
                            <div className="input-field col s12">
                                <label htmlFor="password">Password</label><br></br>
                                <input
                                    onChange={this.onChange}
                                    value={this.state.password}
                                    error={errors.password}
                                    id="password"
                                    type="password"
                                    className={classnames("", {
                                        invalid: errors.password
                                    })}
                                />
                                <span className="red-text">{errors.password}</span>
                            </div>
                            <div className="input-field col s12">
                                <label htmlFor="password2">Confirm Password</label><br></br>
                                <input
                                    onChange={this.onChange}
                                    value={this.state.password2}
                                    error={errors.password2}
                                    id="password2"
                                    type="password"
                                    className={classnames("", {
                                        invalid: errors.password2
                                    })}
                                />
                                <span className="red-text">{errors.password2}</span>
                            </div>
                            <hr></hr>
                            <div className="input-field col s12">
                                {RoleForm}
                            </div>
                            <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                                <button
                                    style={{
                                    width: "150px",
                                    borderRadius: "3px",
                                    letterSpacing: "1.5px",
                                    marginTop: "1rem"
                                    }}
                                    type="submit"
                                    className="btn btn-primary btn-large waves-effect waves-light hoverable blue accent-3"
                                >
                                    Sign up
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

Register.propTypes = {
    registerUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
});

export default connect(
    mapStateToProps,
    { registerUser }
)(withRouter(Register));
