import Tooltip from '@material-ui/core/Tooltip';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import Autocomplete from '@material-ui/lab/Autocomplete';
import IconButton from "@material-ui/core/IconButton";
import InputAdornment from "@material-ui/core/InputAdornment";

import SearchIcon from "@material-ui/icons/Search";
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';

import PropTypes from "prop-types";
import Card from "react-bootstrap/Card";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import Dropdown from 'react-bootstrap/Dropdown';



class JobsList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            userdetails: [],
            jobs: [],
            sortedJobs: [], 
            sortName:true, 
            recruiterName: ""
        };
        // this.renderIcon = this.renderIcon.bind(this);
        // this.sortChange = this.sortChange.bind(this);
    }

    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutUser();
    };

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    componentDidMount() {
        const { user } = this.props.auth;
        axios.get('http://localhost:4000/user/'+ user.id)
                .then(response => {
                    this.setState({userdetails: response.data});
                })
                .catch(function(error) {
                    console.log(error);
                })
        axios.get('http://localhost:4000/job/get_jobs')
            .then(response => {
                this.setState({jobs: response.data, sortedJobs:response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
    }


    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
            errors: nextProps.errors
            });
        }
    }

    apply(job)
    {
        const { user } = this.props.auth;
        e.preventDefault();
        const enteredsop = prompt('PLease enter SOP:');
        const newApplication = {
            jobId: job_id,
            applicantId: user.id,
            recruiterId:job.recruiter,
            stage: 0,
            status: "Initial",
            sop: enteredsop
        };
        const isValid = this.validate();
        if (isValid) {
            axios
                .post('http://localhost:4000/job/add_job', newApplication)
                .then(response => {
                    console.log(newJob);
                    alert("Job added successfully!");
                    // to refresh
                    this.props.history.push("/viewMyActiveJobs");
                    this.props.history.push("/viewMyActiveJobs");
                    this.props.history.goBack();
                })
                .catch(function(error) {
                    console.log(error);
                    alert("Job NOT added successfully!");
                })
        }
    }

    // sortChange(){
    //     var array = this.state.users;
    //     var flag = this.state.sortName;
    //     array.sort(function(a, b) {
    //         if(a.date != undefined && b.date != undefined){
    //             return (1 - flag*2) * (new Date(a.date) - new Date(b.date));
    //         }
    //         else{
    //             return 1;
    //         }
    //       });
    //     this.setState({
    //         users:array,
    //         sortName:!this.state.sortName,
    //     })
    // }

    // renderIcon(){
    //     if(this.state.sortName){
    //         return(
    //             <ArrowDownwardIcon/>
    //         )
    //     }
    //     else{
    //         return(
    //             <ArrowUpwardIcon/>
    //         )            
    //     }
    // }

    render() 
    {
        const user = this.state;
        const userRole = this.state.userdetails.role;
        const userid = this.state.userdetails._id;
        let AllJobs;
        if(userRole == "applicant") {
            AllJobs =
            <div>
                <Grid container>
                <Grid item xs={12} md={3} lg={3}>
                    <List component="nav" aria-label="mailbox folders">
                        <ListItem text>
                            <h3>Active Jobs</h3>
                        </ListItem>
                    </List>
                </Grid>
                    {/* <Grid item xs={12} md={9} lg={9}>
                    <List component="nav" aria-label="mailbox folders">
                        <TextField 
                        id="standard-basic" 
                        label="Search" 
                        fullWidth={true}   
                        InputProps={{
                            endAdornment: (
                                <InputAdornment>
                                    <IconButton>
                                        <SearchIcon />
                                    </IconButton>
                                </InputAdornment>
                            )}}
                        />
                    </List>
                    </Grid> */}
                </Grid>
                <Grid container>
                    {/* <Grid item xs={12} md={3} lg={3}>
                        <List component="nav" aria-label="mailbox folders">

                            <ListItem button>
                                <form noValidate autoComplete="off">
                                    <label>Salary</label>
                                    <TextField id="standard-basic" label="Enter Min" fullWidth={true} />
                                    <TextField id="standard-basic" label="Enter Max" fullWidth={true}/>
                                </form>                                                                
                            </ListItem>
                            <Divider />
                            <ListItem button divider>
                                <Autocomplete
                                    id="combo-box-demo"
                                    options={this.state.jobs}
                                    getOptionLabel={(option) => option.name}
                                    style={{ width: 300 }}
                                    renderInput={(params) => <TextField {...params} label="Select Names" variant="outlined" />}
                                />
                            </ListItem>
                        </List>
                    </Grid> */}
                    <Grid item xs={12} md={9} lg={9}>
                        <Paper>
                            <Table size="small">
                                <TableHead>
                                    <TableRow>
                                            {/* <TableCell> <Button onClick={this.sortChange}>{this.renderIcon()}</Button>Date</TableCell> */}
                                            <TableCell>Title</TableCell>
                                            <TableCell>Recruiter</TableCell>
                                            <TableCell>Rating</TableCell>
                                            <TableCell>Salary</TableCell>
                                            <TableCell>Duration(months)</TableCell>
                                            <TableCell>Deadline</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {this.state.jobs.filter(item => (new Date()).getTime() <= (new Date(item.deadline.substring(0,10))).getTime()).map((job,ind) => (
                                        <TableRow key={ind}>
                                            <TableCell>{job.title}</TableCell>
                                            <TableCell>{job.recruiterName}</TableCell>
                                            <TableCell>{job.rating}</TableCell>
                                            <TableCell>{job.salary}</TableCell>
                                            <TableCell>{job.duration}</TableCell>
                                            <TableCell>{job.deadline.substring(0,10)}</TableCell>
                                            <TableCell>
                                                <Tooltip title="Apply for this job" aria-label="apply">
                                                    <button
                                                        className="btn btn-primary btn-sm waves-effect waves-light hoverable blue accent-3" 
                                                        onClick={() => this.apply(job._id)}>
                                                        Apply
                                                    </button> 
                                                </Tooltip>                                            
                                            </TableCell>
                                        </TableRow> 
                                ))}
                                </TableBody>
                            </Table>
                        </Paper>               
                    </Grid>    
                </Grid>            
            </div>
        }
        return (
            <div style={{ height: "75vh" }} className="container valign-wrapper">
                <div className="row">
                    <div className="col s12 center-align">
                        <Card style={{ width: '100%' }}>
                            <Card.Body>
                                <Card.Text>
                                    {AllJobs}
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </div>
                </div>
            </div>
        );
    }
}

JobsList.propTypes = {
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
});
export default connect(
    mapStateToProps,
)(JobsList);
