import React, {Component} from 'react';
import axios from 'axios';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import List from '@material-ui/core/List';
import Tooltip from '@material-ui/core/Tooltip';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import Autocomplete from '@material-ui/lab/Autocomplete';
import IconButton from "@material-ui/core/IconButton";
import InputAdornment from "@material-ui/core/InputAdornment";

import SearchIcon from "@material-ui/icons/Search";
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';

import PropTypes from "prop-types";
import Card from "react-bootstrap/Card";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import Dropdown from 'react-bootstrap/Dropdown';



class MyApplications extends Component {

    constructor(props) {
        super(props);
        this.state = {
            userdetails: [],
            applications: [],
            showForm: false,
            editing: "",
            enteredsop: "",
            sopError: ""
        };
        // this.renderIcon = this.renderIcon.bind(this);
        // this.sortChange = this.sortChange.bind(this);
    }

    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutUser();
    };

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    componentDidMount() {
        const { user } = this.props.auth;
        this.state.showForm = false;
        this.state.editing = "";
        this.state.enteredsop = "";
        axios.get('http://localhost:4000/user/'+ user.id)
                .then(response => {
                    this.setState({userdetails: response.data});
                })
                .catch(function(error) {
                    console.log(error);
                })
        axios.get('http://localhost:4000/job/get_jobs')
            .then(response => {
                this.setState({jobs: response.data, sortedJobs:response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
        axios.get('http://localhost:4000/application/get_applications')
            .then(response => {
                this.setState({applications: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
    }


    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
            errors: nextProps.errors
            });
        }
    }

    rated(job) {
        const { user } = this.props.auth;
        let num = 0;
        let arr = this.state.applications.filter(item => item.jobId === job._id && item.applicantId === user.id && (item.status !== "Rejected" || item.status != "Accepted"));
        num = arr.length;
        if(num>0) return true;
        else return false;
    }

    rate(job)
    {
        this.state.showForm = !this.state.showForm;
        this.state.editing = job._id;
        //this.setState({showform: ShowForm});
        console.log(this.state.showForm);
        this.props.history.push("/jobsList");
        this.props.history.push("/jobsList");
        this.props.history.goBack();
    }

    onSOPsubmit(job)
    {
        const { user } = this.props.auth;
        const newApplication = {
            jobId: job._id,
            applicantId: user.id,
            recruiterId:job.recruiter,
            stage: 0,
            status: "Applied",
            sop: this.state.enteredsop
        };

        let num = this.state.enteredsop.split(' ').length;
        if(num <= 250)
        {
            axios
                .post('http://localhost:4000/application/add_application', newApplication)
                .then(response => {
                    console.log(newApplication);
                    alert("Application sent successfully!");
                    // to refresh
                    this.state.editing = "";
                    this.props.history.push("/jobsList");
                    this.props.history.push("/jobsList");
                    this.props.history.goBack();
                })
                .catch(function(error) {
                    console.log(error);
                    alert("Applicantion could not be sent.");
                })
            this.state.editing = "";
            this.props.history.push("/jobsList");
            this.props.history.push("/jobsList");
            this.props.history.goBack();
        }
        else{
            let soperror = "Maximum 250 words allowed.";
            this.setState({sopError: soperror});
        }
    }

    render() 
    {
        const { user } = this.props.auth;
        const userRole = this.state.userdetails.role;
        let AppliedJobs;
        if(userRole === "applicant") {
            AppliedJobs =
            <div>
                <Grid container>
                <Grid item xs={12} md={3} lg={3}>
                    <List component="nav" aria-label="mailbox folders">
                        <ListItem text>
                            <h3>My Applications</h3>
                        </ListItem>
                    </List>
                </Grid>
                </Grid>
                <Grid container>
                    <Grid item xs={12} md={2} lg={9}>
                        <Paper>
                            <Table size="small">
                                <TableHead>
                                    <TableRow>
                                            <TableCell>Title</TableCell>
                                            <TableCell>Recruiter</TableCell>
                                            <TableCell>Rating</TableCell>
                                            <TableCell>Salary</TableCell>
                                            <TableCell>Duration(months)</TableCell>
                                            <TableCell>Deadline</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {this.state.applications.filter(item => item.applicantId === user.id.map((job,ind) => (
                                        <TableRow key={ind}>
                                            <TableCell>{job.title}</TableCell>
                                            <TableCell>{job.recruiterName}</TableCell>
                                            <TableCell>{job.rating}</TableCell>
                                            <TableCell>{job.salary}</TableCell>
                                            <TableCell>{job.duration}</TableCell>
                                            <TableCell>{job.deadline.substring(0,10)}</TableCell>
                                            {!this.rated(job) && job._id !== this.state.editing?

                                            <TableCell>
                                                <Tooltip title="Apply for this job" aria-label="apply">
                                                    <button
                                                        className="btn btn-primary btn-sm waves-effect waves-light hoverable blue accent-3" 
                                                        onClick={() => this.rate(job)}>
                                                        Rate
                                                    </button> 
                                                </Tooltip>                                            
                                            </TableCell>

                                            :

                                            <TableCell>
                                                <Tooltip title="Applied for this job" aria-label="applied">
                                                    <button
                                                        className="btn btn-secondary disabled btn-sm waves-effect waves-light hoverable blue accent-3">
                                                        Rated
                                                    </button> 
                                                </Tooltip>                                            
                                            </TableCell>

                                            }

                                        </TableRow>
                                    )))}
                                </TableBody>
                            </Table>
                        </Paper>               
                    </Grid>    
                </Grid>            
            </div>
        }
        return (
            <div style={{ height: "75vh" }} className="container valign-wrapper">
                <div className="row">
                    <div className="col s12 center-align">
                        <Card style={{ width: '100%' }}>
                            <Card.Body>
                                <Card.Text>
                                    {AppliedJobs}
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </div>
                </div>
            </div>
        );
    }
}

MyApplications.propTypes = {
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
});
export default connect(
    mapStateToProps,
)(MyApplications);
