import React, { Component } from "react";
import axios from 'axios';
import Card from "react-bootstrap/Card";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class CreateJob extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userdetails: [],
            recruiter: [],
            title: "",
            titleError: "",
            description: "",
            type: "",
            typeError: "",
            duration: -1,
            durationError: "",
            durationstr: "",
            salary: -1,
            salaryError: "",
            // default max no of pos and app = 10
            pos_m: -1,
            app_m: -1,
            app: 0,
            address: "",
            addressError: "",
            skills: [],
            skillstr: "",
            rating: 5,
            dateOfPost: new Date(),
            // default 100 days from now
            deadline: new Date(new Date().getTime()+(100*24*60*60*1000))
        }
    };

    componentDidMount() {
        const { user } = this.props.auth;
        axios.get('http://localhost:4000/user/'+ user.id)
             .then(response => {
                 this.setState({userdetails: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    validate = () => {
        let titleError = "";
        let typeError = "";
        let durationError = "";
        let salaryError = "";
        let addressError = "";


        if (this.state.title == "") {
          titleError = "Title cannot be blank";
        }

        if (this.state.type == "") {
            typeError = "Select type of job";
        }

        if (this.state.duration == -1) {
            durationError = "Select duration of job";
        }

        if (this.state.salary == -1) {
            salaryError = "Enter Salary";
        }

        if (this.state.address == "") {
            addressError = "Address cannot be blank";
        }

        if (titleError || typeError || durationError || salaryError
            || addressError) {
            this.setState({ titleError, typeError, durationError, salaryError, addressError });
            return false;
        }

        return true;
    };

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
            errors: nextProps.errors
            });
        }
    }

    onSubmit = e => {
        e.preventDefault();
        let euser = this.state;
        euser.skills = this.state.skillstr.split(',');
        if(euser.durationstr != "")
        {
            euser.duration = parseInt(euser.durationstr);
        }
        if(euser.mxpos == -1)
            euser.mxpos = 10;
        if(euser.mxapp == -1)
            euser.mxapp = 10;
        const newJob = {
            recruiter: this.state.userdetails,
            title: euser.title,
            description: euser.description,
            type: euser.type,
            duration: euser.duration,
            salary: euser.salary,
            pos_m: euser.pos_m,
            app_m: euser.app_m,
            app: euser.app,
            address: euser.address,
            skills: euser.skills,
            rating: euser.rating,
            dateOfPost: euser.dateOfPost,
            deadline: euser.deadline
        };
        const isValid = this.validate();
        if (isValid) {
            axios
                .post('http://localhost:4000/job/add_job', newJob)
                .then(response => {
                    console.log(newJob);
                    alert("Job added successfully!");
                    // to refresh
                    this.props.history.push("/viewJobs");
                    this.props.history.push("/viewJobs");
                    this.props.history.goBack();
                })
                .catch(function(error) {
                    console.log(error);
                    alert("Job NOT added successfully!");
                })
        }
    };

    render() {
        const user = this.state;
        const userRole = this.state.userdetails.role;
        let AddJob;
        if(userRole == "recruiter") {
            AddJob = 
            <form noValidate onSubmit={this.onSubmit}>
                <div className="input-field col s12">
                    <label htmlFor="title">Title</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.title}
                        id="title"
                        type="text"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.titleError}
                    </div>
                </div>
                <div className="input-field col s12">
                    <label htmlFor="description">Description</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.description}
                        id="description"
                        type="text"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="type">Type</label><br></br>
                    <select 
                        value={this.state.type} 
                        onChange={this.onChange}
                        id="type"
                    >
                        <option value="">Select type</option>
                        <option value="fullTime">Full-time</option>
                        <option value="partTime">Part-time</option>
                        <option value="wfh">Work from home</option>
                    </select>
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.typeError}
                    </div>
                </div> 
                <div className="input-field col s12">
                    <label htmlFor="salary">Salary</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.salary}
                        id="salary"
                        type="number"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.salaryError}
                    </div>
                </div>
                <div className="input-field col s12">
                    <label htmlFor="durationstr">Duration</label><br></br>
                    <select 
                        value={this.state.durationstr} 
                        onChange={this.onChange}
                        id="durationstr"
                    >
                        <option value="">Select duration</option>
                        <option value = "0" >Indefinite</option>
                        <option value="1">1 month</option>
                        <option value="2">2 months</option>
                        <option value="3">3 months</option>
                        <option value="4">4 months</option>
                        <option value="5">5 months</option>
                        <option value="6">6 months</option>
                    </select>
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.durationError}
                    </div>
                </div> 
                <div className="input-field col s12">
                    <label htmlFor="pos_m">Max. no. of positions available</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.mxpos}
                        id="pos_m"
                        type="number"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="app_m">Max. total no. applications allowed</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.app_m}
                        id="app_m"
                        type="number"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="address">Address</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.address}
                        id="address"
                        type="text"
                    />
                    <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.addressError}
                    </div>
