import React, { Component } from "react";
import axios from 'axios';
import Card from "react-bootstrap/Card";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class CreateJob extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userdetails: [],
            recruiter: [],
            title: "", //
            description: "",
            type: "", //
            duration: -1, //
            salary: -1, //
            maxpos: -1,
            maxapp: -1,
            address: "", //
            skills: [], //
            skillsstring: "",
            rating: 5,
            dateOfPost: new Date(),
            deadline: new Date() //
        }
    };

    componentDidMount() {
        const { user } = this.props.auth;
        axios.get('http://localhost:4000/user/'+ user.id)
             .then(response => {
                 this.setState({userdetails: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    validate = () => {
        // let nameError = "";
        // let emailError = "";

        // if (!this.state.name) {
        //   nameError = "Name cannot be blank";
        // }

        // if (!this.state.email.includes("@")) {
        //   emailError = "Invalid email";
        // }

        // if (emailError || nameError) {
        //   this.setState({ emailError, nameError });
        //   return false;
        // }

        return true;
    };

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    };

    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
            this.setState({
            errors: nextProps.errors
            });
        }
    }

    onSubmit = e => {
        e.preventDefault();
        const { user } = this.props.auth;
        const euser = this.state;
        euser.skills = this.state.skillsstring.split(',');
        const newJob = {
            recruiter: userdetails,
            title: euser.title,
            description: euser.description,
            type: euser.type,
            duration: euser.duration,
            salary: euser.salary,
            maxpos: euser.maxpos,
            maxapp: euser.maxapp,
            address: euser.address,
            skills: euser.skills,
            rating: euser.rating,
            dateOfPost: euser.dateOfPost,
            deadline: euser.deadline
        };
        const isValid = this.validate();
        if (isValid) {
            axios
                .post('http://localhost:4000/job/add_job', newJob)
                .then(response => {
                    console.log(newJob);
                })
                .catch(function(error) {
                    console.log(error);
                })
            // to refresh
            this.props.history.push("/viewJobs");
            this.props.history.push("/viewJobs");
            this.props.history.goBack();
        }
    };

    render() {
        const user = this.state;
        const userRole = this.state.userdetails.role;
        let AddJob;
        if(userRole == "recruiter") {
            AddJob = 
            <form noValidate onSubmit={this.onSubmit}>
                <div className="input-field col s12">
                    <label htmlFor="title">Title</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.title}
                        id="title"
                        type="text"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="description">Description</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.description}
                        id="description"
                        type="text"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="type">Type</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.type}
                        id="type"
                        type="text"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="salary">Salary</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.salary}
                        id="salary"
                        type="number"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="maxpos">Max. no. of positions available</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.name}
                        id="maxpos"
                        type="number"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="maxapp">Max. total no. applications allowed</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.maxapp}
                        id="maxapp"
                        type="number"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="address">Address</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.address}
                        id="address"
                        type="text"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="skills">Skills</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.skillsstring}
                        id="name"
                        type="skills"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="dateOfPost">Date of Post</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.dateOfPost}
                        id="dateOfPost"
                        type="date"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="input-field col s12">
                    <label htmlFor="deadline">Deadline</label><br></br>
                    <input
                        onChange={this.onChange}
                        value={user.deadline}
                        id="deadline"
                        type="date"
                    />
                    {/* <div style={{ fontSize: 12, color: "red" }}>
                        {this.state.nameError}
                    </div> */}
                </div>
                <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                    <button
                        style={{
                        width: "150px",
                        borderRadius: "3px",
                        letterSpacing: "1.5px",
                        marginTop: "1rem"
                        }}
                        type="submit"
                        className="btn btn-primary btn-large waves-effect waves-light hoverable blue accent-3"
                    >
                        Add Job
                    </button>
                </div>
            </form>
        }

        return (
            <div style={{ height: "75vh" }} className="container valign-wrapper">
                <div className="row">
                    <div className="col s12 center-align">
                        <Card style={{ width: '100%' }}>
                            <Card.Body>
                                <Card.Text>
                                    {AddJob}
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </div>
                </div>
            </div>
        );
    }
}

CreateJob.propTypes = {
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
});
export default connect(
    mapStateToProps,
)(CreateJob);
